import React, { useEffect } from 'react';
import { CaseStudyHero } from '../components/CaseStudyHero';
import CaseStudySection from '../components/CaseStudySection';
import { CaseStudyApproach } from '../components/CaseStudyApproach';
import { CaseStudyParagraph } from '../components/CaseStudyParagraph';
import { getImagePath } from '../utils/imageUtils';
import './Nova_ai.css';

const NovaAI = () => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <div className="case-study-nova-ai">
      <div className="container">
        <CaseStudyHero
          title="Nova AI"
          subtitle="Leveraging LLMs to automate exercise generation for adaptive learning"
          services={['Concepting', 'Product Design']}
          client="Nova"
          year="2024"
        />

        <div className="content-wrapper">
          <div className="full-width-image">
            <img src={getImagePath('nova ai 01.png')} alt="Nova AI hero image" />
          </div>

          <CaseStudyParagraph
            title="The challenge"
            content={[
              "As the Principal Product Designer at Otava Learning, my primary objective was to create a tool that would help to streamline our team's tasks and the innovative solutions we developed through a proof of concept that creates adaptive learning exercises. The goal was to leverage large language models (LLMs) to automate the generation of exercises for differentiated learning materials across all grade levels and subject areas."
            ]}
          />

          <div className="image-row">
            <div className="image-column">
              <img src={getImagePath('nova ai 02.png')} alt="Nova AI concept 1" />
            </div>
            <div className="image-column">
              <img src={getImagePath('nova ai 03.png')} alt="Nova AI concept 2" />
            </div>
            <div className="image-column">
              <img src={getImagePath('nova ai 04.png')} alt="Nova AI concept 3" />
            </div>
          </div>

          {/* Rest of the component remains the same */}
        </div>
      </div>
    </div>
  );
};

export default NovaAI;
