import React from 'react';
import './Footer.css'; // Make sure to create and import appropriate styles

function Footer({ currentPath, isLight }) {
  // List of paths where we don't want to show the footer
  const excludePaths = ['/work/NovaBrand', '/work/Hel_fi', '/work/FiraBrand'];

  if (excludePaths.includes(currentPath)) {
    return null;
  }

  return (
    <footer className={isLight ? 'light-theme' : 'dark-theme'}>
      <div className="footer-content">
        <p>Quality rarely happens by accident. You have to work for it.</p>
        <a href="/contact" className="collab-link">Let's collab</a>
      </div>
    </footer>
  );
}

export default Footer;
