import React, { useEffect, useRef } from 'react';
import './CaseStudyHero.css'; // We'll create this CSS file next

function CaseStudyHero({ title, subtitle, services, client, year }) {
  const titleRef = useRef(null);
  const containerRef = useRef(null);

  useEffect(() => {
    const adjustFontSize = () => {
      const titleElement = titleRef.current;
      const containerElement = containerRef.current;
      if (!titleElement || !containerElement) return;

      let fontSize = 160; // Starting font size (10rem assuming 16px base font size)
      titleElement.style.fontSize = `${fontSize}px`;

      while (titleElement.offsetWidth > containerElement.offsetWidth && fontSize > 32) {
        fontSize -= 1;
        titleElement.style.fontSize = `${fontSize}px`;
      }
    };

    adjustFontSize();
    window.addEventListener('resize', adjustFontSize);

    return () => window.removeEventListener('resize', adjustFontSize);
  }, [title]);

  return (
    <header className="case-study-header">
      <div className="case-study-title-container" ref={containerRef}>
        <h1 className="case-study-title" ref={titleRef}>{title}</h1>
      </div>
      <div className="case-study-hero-content">
        <div className="case-study-intro">
          <p className="case-study-subtitle">{subtitle}</p>
          <div className="case-study-details">
            <div className="case-study-detail services">
              <h3>SERVICES</h3>
              <p>
                {services.map((service, index) => (
                  <span key={index} className="pill">{service}</span>
                ))}
              </p>
            </div>
            <div className="case-study-detail client">
              <h3>CLIENT</h3>
              <p>{client}</p>
            </div>
            <div className="case-study-detail year">
              <h3>YEAR</h3>
              <p>{year}</p>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
}

export default CaseStudyHero;
