import React from 'react';
import './Testimonials.css';

function Testimonials({ title, testimonials }) {
  return (
    <section className="testimonials">
      <h2>{title}</h2>
      <div className="testimonials-carousel">
        {testimonials.map((testimonial, index) => (
          <div key={index} className="testimonial-card">
            <p className="testimonial-quote">{testimonial.quote}</p>
            <div className="testimonial-author">
              <div className="author-info">
                <p className="author-name">{testimonial.author}</p>
                <p className="author-position">{testimonial.position}</p>
              </div>
              <img src={testimonial.avatar} alt={testimonial.author} className="author-avatar" />
            </div>
          </div>
        ))}
      </div>
      <div className="carousel-controls">
        <button className="carousel-control prev">←</button>
        <button className="carousel-control next">→</button>
      </div>
    </section>
  );
}

export default Testimonials;
