import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Home from './pages/Home';
import NovaBrand from './pages/NovaBrand';
import Header from './components/Header';
import Footer from './components/Footer';
import SocialDock from './components/SocialDock';
import Hel_fi from './pages/Hel_fi';
import './GlobalStyles.css';
import FiraBrand from './pages/FiraBrand';
import Work from './pages/Work';
import About from './pages/About';

function App() {
  return (
    <div className="app-wrapper" style={{minHeight: '100vh'}}>
      <Router>
        <AppContent />
      </Router>
    </div>
  );
}

function AppContent() {
  const location = useLocation();
  const isCaseStudyPage = location.pathname.includes('/work/');

  return (
    <>
      <Header currentPath={location.pathname} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/work/NovaBrand" element={<NovaBrand />} />
        <Route path="/work" element={<Work />} />
        <Route path="/about" element={<About />} />
        <Route path="/work/Hel_fi" element={<Hel_fi />} />
        <Route path="/work/FiraBrand" element={<FiraBrand />} />
      </Routes>
      <Footer currentPath={location.pathname} />
      <SocialDock />
    </>
  );
}

export default App;
