import React from 'react';
import { CaseStudyHero } from '../components/CaseStudyHero';
import CaseStudySection from '../components/CaseStudySection';
import { CaseStudyApproach } from '../components/CaseStudyApproach';
import { CaseStudyParagraph } from '../components/CaseStudyParagraph';
import { getImagePath } from '../utils/imageUtils';
import { CaseStudyVideo } from '../components/CaseStudyVideo';
import { CaseStudySteps } from '../components/CaseStudySteps';
import './Nova_platform.css';

const NovaPlatform = () => {
  return (
    <div className="case-study-nova-platform">
      <h1>Nova Platform</h1>
      <div className="container">
        <CaseStudyHero
          title="Nova Platform"
          subtitle="I led the design vision of Otava Learning's new online learning platform with a focus on inspiring and functional learning experiences."
          services={['Product Design', 'UX Research', 'Design Leadership']}
          client="Nova"
          year="2023"
        />

        <div className="full-width-image">
          <img src={getImagePath('nova-platform-01.png')} alt="Nova Platform hero image" />
        </div>

        <CaseStudyParagraph
          title="Revolutionizing Digital Learning for Every Student"
          content={[
            "As the Principal Product Designer at Nova Learning, our goal was to create a comprehensive digital learning platform that caters to diverse learning needs and enhances the educational experience for students of all ages."
          ]}
        />

        <CaseStudySteps
          title="Key Challenges"
          steps={[
            {
              title: "Accessibility",
              description: "Ensure the platform is accessible to students with diverse needs and abilities."
            },
            {
              title: "Engagement",
              description: "Create an engaging and interactive learning experience to keep students motivated."
            },
            {
              title: "Personalization",
              description: "Develop a system that adapts to individual learning styles and paces."
            },
            {
              title: "Integration",
              description: "Seamlessly integrate various learning resources and tools into a unified platform."
            }
          ]}
        />

        <CaseStudySection
          title="Our approach"
          description1="We adopted a user-centered design approach, conducting extensive research with students, teachers, and parents to understand their needs and pain points."
          description2="Our team then iteratively designed and tested prototypes, refining the platform based on user feedback and data-driven insights."
        />

        <CaseStudyVideo
          videoSrc="/path/to/nova_platform_demo.mp4"
          posterSrc={getImagePath('nova_platform_video_poster.png')}
          title="A better future for every learner."
        />

        <CaseStudyApproach
          title="Key features of Nova Platform"
          image={getImagePath('nova_platform_features.png')}
          imageAlt="Nova Platform features illustration"
          steps={[
            {
              title: "Adaptive Learning Paths",
              description: "Personalized learning journeys that adjust to each student's progress and learning style."
            },
            {
              title: "Interactive Content",
              description: "Engaging multimedia lessons, quizzes, and activities to enhance understanding and retention."
            },
            {
              title: "Progress Tracking",
              description: "Comprehensive analytics for students, teachers, and parents to monitor growth and identify areas for improvement."
            },
            {
              title: "Collaborative Tools",
              description: "Features that facilitate peer-to-peer learning and teacher-student interactions."
            }
          ]}
        />

        <CaseStudyParagraph
          title="The impact"
          content={[
            "The Nova Platform has transformed the digital learning landscape, providing an inclusive and effective solution for students across various grade levels and subjects.",
            "Early adoption results show a significant increase in student engagement, improved learning outcomes, and positive feedback from educators and parents alike.",
            "The platform's success has paved the way for partnerships with educational institutions worldwide, furthering our mission to provide quality education for all."
          ]}
        />

        <div className="image-row">
          <div className="image-column">
            <img src={getImagePath('nova_platform_impact1.png')} alt="Nova Platform impact 1" />
          </div>
          <div className="image-column">
            <img src={getImagePath('nova_platform_impact2.png')} alt="Nova Platform impact 2" />
          </div>
        </div>

        <CaseStudyParagraph
          title="Looking ahead"
          content={[
            "As we continue to evolve the Nova Platform, we're exploring cutting-edge technologies such as AI-powered tutoring, virtual reality experiences, and advanced data analytics to further personalize and enhance the learning journey.",
            "Our team remains committed to pushing the boundaries of digital education, ensuring that every learner has access to high-quality, engaging, and effective educational resources."
          ]}
        />
      </div>
    </div>
  );
};

export default NovaPlatform;
